css:
.preview-art-deco {
    background: #000;
    color: #ffd700;
    font-family: 'Cinzel', serif;
    border: 5px solid #ffd700;
    position: relative;
    overflow: hidden;
  }

  .preview-art-deco::before {
    content: '';
    position: absolute;
    top: -50%;
    left: -50%;
    width: 200%;
    height: 200%;
    background: linear-gradient(45deg, #ffd700 25%, transparent 25%, transparent 50%, #ffd700 50%, #ffd700 75%, transparent 75%);
    background-size: 20px 20px;
    opacity: 0.1;
    animation: artDecoPattern 10s linear infinite;
  }

  .preview-art-deco h1 {
    font-size: 3em;
    text-transform: uppercase;
    letter-spacing: 2px;
    margin-bottom: 20px;
    position: relative;
    text-shadow: 2px 2px 0px rgba(0,0,0,0.5);
    animation: titleGlow 2s ease-in-out infinite;
  }

  .preview-art-deco p {
    font-size: 1.2em;
    font-style: italic;
    margin-bottom: 30px;
    opacity: 0.8;
  }

  .preview-art-deco button {
    background: #ffd700;
    color: #000;
    border: none;
    padding: 15px 40px;
    font-family: inherit;
    font-size: 1em;
    cursor: pointer;
    position: relative;
    overflow: hidden;
    transition: all 0.3s ease;
    transform-style: preserve-3d;
  }

  .preview-art-deco button::before {
    content: '';
    position: absolute;
    top: 0;
    left: -100%;
    width: 100%;
    height: 100%;
    background: linear-gradient(120deg, transparent, rgba(255,255,255,0.3), transparent);
    transition: all 0.5s ease;
  }

  .preview-art-deco button:hover {
    transform: translateY(-3px) scale(1.02);
    box-shadow: 0 5px 15px rgba(255,215,0,0.4);
  }

  .preview-art-deco button:hover::before {
    left: 100%;
  }

  @keyframes artDecoPattern {
    0% { transform: rotate(0deg); }
    100% { transform: rotate(360deg); }
  }

  @keyframes titleGlow {
    0%, 100% { text-shadow: 2px 2px 0px rgba(0,0,0,0.5); }
    50% { text-shadow: 2px 2px 20px rgba(255,215,0,0.5); }
  }

.preview-neural-network {
    background: #0a0320;
    color: #fff;
    font-family: 'Space Grotesk', sans-serif;
    position: relative;
    overflow: hidden;
  }

  .preview-neural-network::before {
    content: '';
    position: absolute;
    inset: 0;
    background: radial-gradient(2px 2px at 50% 50%, #5b49ff 50%, transparent);
    animation: neuralNodes 20s linear infinite;
    opacity: 0.5;
  }

  .preview-neural-network::after {
    content: '';
    position: absolute;
    inset: 0;
    background: radial-gradient(2px 2px at 50% 50%, #49c7ff 50%, transparent);
    animation: neuralNodes 20s linear infinite reverse;
    opacity: 0.3;
  }

  .preview-neural-network h1 {
    font-size: 3.2em;
    background: linear-gradient(120deg, #5b49ff, #49c7ff);
    -webkit-background-clip: text;
    background-clip: text;
    color: transparent;
    text-shadow: 0 0 20px rgba(91, 73, 255, 0.5);
    position: relative;
    z-index: 2;
    animation: neuralGlow 4s ease-in-out infinite;
  }

  .preview-neural-network p {
    color: rgba(255,255,255,0.8);
    text-shadow: 0 0 10px rgba(73, 199, 255, 0.5);
    position: relative;
    z-index: 2;
  }

  .preview-neural-network button {
    background: transparent;
    border: 2px solid #5b49ff;
    color: #fff;
    padding: 15px 40px;
    position: relative;
    overflow: hidden;
    transition: all 0.3s;
    z-index: 2;
    cursor: pointer;
  }

  .preview-neural-network button:hover {
    background: rgba(91, 73, 255, 0.2);
    transform: translateY(-2px);
    box-shadow: 0 5px 15px rgba(91, 73, 255, 0.4);
  }

  @keyframes neuralNodes {
    0%, 100% { background-size: 100px 100px; transform: rotate(0deg); }
    50% { background-size: 150px 150px; transform: rotate(180deg); }
  }

  @keyframes neuralGlow {
    0%, 100% { text-shadow: 0 0 20px rgba(91, 73, 255, 0.5); }
    50% { text-shadow: 0 0 40px rgba(91, 73, 255, 0.8); }
  }

.preview-quantum-flux {
    background: linear-gradient(45deg, #000033, #330066);
    color: #fff;
    font-family: 'Syncopate', sans-serif;
    position: relative;
    overflow: hidden;
  }

  .preview-quantum-flux::before {
    content: '';
    position: absolute;
    inset: 0;
    background: repeating-linear-gradient(45deg, transparent, transparent 20px, rgba(255,255,255,0.1) 20px, rgba(255,255,255,0.1) 40px);
    animation: quantumShift 20s linear infinite;
    opacity: 0.5;
  }

  .preview-quantum-flux h1 {
    font-size: 2.8em;
    background: linear-gradient(120deg, #00ffff, #ff00ff);
    -webkit-background-clip: text;
    background-clip: text;
    color: transparent;
    text-shadow: 0 0 15px rgba(255,255,255,0.5);
    position: relative;
    z-index: 2;
    animation: quantumPulse 4s ease-in-out infinite;
  }

  .preview-quantum-flux p {
    color: rgba(255,255,255,0.9);
    text-shadow: 0 0 8px rgba(0,255,255,0.8);
    position: relative;
    z-index: 2;
    letter-spacing: 2px;
  }

  .preview-quantum-flux button {
    background: transparent;
    border: 2px solid #00ffff;
    color: #fff;
    padding: 15px 40px;
    position: relative;
    overflow: hidden;
    transition: all 0.4s;
    z-index: 2;
    cursor: pointer;
    backdrop-filter: blur(5px);
  }

  .preview-quantum-flux button:hover {
    background: rgba(0,255,255,0.2);
    transform: translateY(-2px) scale(1.05);
    box-shadow: 0 0 20px rgba(0,255,255,0.4);
  }

  @keyframes quantumShift {
    0% { transform: perspective(500px) rotateX(0deg) rotateY(0deg); }
    100% { transform: perspective(500px) rotateX(360deg) rotateY(360deg); }
  }

  @keyframes quantumPulse {
    0%, 100% { opacity: 0.8; transform: scale(1); }
    50% { opacity: 1; transform: scale(1.05); }
  }
